function string.join(sep, array, prefix, suffix)
  local s = ""
  for i, v in ipairs(array) do
    if i ~= 1 then s = s .. sep end
    s = s .. utils.tostring(v)
  end
  return (prefix or "") .. s .. (suffix or "")
end

function string.unwindHex(s)
  local palette = {}
  for i = 1, #s, 2 do
    local hex = string.sub(s, i, i + 1)
    table.insert(palette, tonumber(hex, 16))
  end
  return palette
end
