--[[
Returns the sign of the arg: 1.0 if the argument is greater than zero, -1.0 if the argument is less than zero.
If the argument is positive zero or negative zero, then the result is the same as the argument.
]]
function math.sign(num) return num <0 and -1 or num >0 and 1 or num end

function math.roundInt(num)
  if num >= 0 then return math.floor(num + 0.5) else return math.ceil(num - 0.5) end
end

function math.round(num, decimals)
  decimals = math.pow(10, decimals or 0)
  return math.roundInt(num*decimals)/decimals
end

function math.clamp(value, min, max)
  if value < min then return min
  elseif value > max then return max
  else return value end
end

function math.clampInt(value, min, max)
  return math.round(math.clamp(value, min, max))
end

function math.clampByte(value)
  return math.roundInt(math.clamp(value, 0, 255))
end
Image = {}
Image.__index = Image

function Image:new(pixels, palette, width, height)
  local inst = setmetatable({}, Image)
  inst.pixels = pixels
  inst.palette = palette
  inst.width = width
  inst.height = height
  return inst
end

-- x and y must be integers
function Image:pix(x, y)
  -- return self.pixels[1 + math.floor(y) * self.width + math.floor(x)]
  return self.pixels[1 + y * self.width + x]
end

function Image:nbColors()
  return #self.palette/3
end

function Image:color(n)
  local offset = n*3
  return self.palette[offset+1], self.palette[offset+2], self.palette[offset+3]
end

function Image:draw(dx, dy, pixFunc, filterFunc)
  self:drawRect(0, 0, self.width, self.height, dx, dy, pixFunc, filterFunc)
end

function Image:drawRect(sx, sy, sw, sh, dx, dy, pixFunc, filterFunc)
  local yMax = sy+sh-1
  local xMax = sx+sw-1
  for y=sy, yMax do
    for x=sx, xMax do
      local col = self:pix(x, y)
      if not filterFunc or filterFunc(x, y, col) then
        -- pixFunc(dx+x-sx, dy+y-sy, col)
        pix(dx+x-sx, dy+y-sy, col) --FIXME this is a bypass to improve perfs
      end
    end
  end
end

function Image:tile(dx, dy, dw, dh, pixFunc)
  for y = 0, dh-1 do
    local sy = y%self.height
    for x = 0, dw-1 do
      local sx = x%self.width
      pixFunc(dx+x, dy+y, self:pix(sx, sy))
    end
  end
end
Stream = {}
Stream.__index = Stream

function Stream:new(tab, from, till)
  local inst = setmetatable({}, Stream)
  inst.tab = tab
  inst.curPos = from or 0
  inst.endPos = till or #tab

  inst.read = function()
    if inst.curPos < inst.endPos then
      inst.curPos = inst.curPos + 1
      local v = tab[inst.curPos]
      return v
    end
  end
  return inst
end

function Stream:pos()
  return self.curPos
end

function Stream:limit()
  return self.endPos
end

function Stream:unwind()
  return self.tab, self:pos()
end

function Stream:readN(n)
  local res = {}
  for _=1,n do table.insert(res, self:read()) end
  return res
end

function Stream:skip()
  self:read()
end

function Stream:skipN(n)
  for _=1,n do self:skip() end
end
Buffer = {}
Buffer.__index = Buffer

function Buffer:new(limit)
  local inst = setmetatable({}, Buffer)
  inst.tab = {}
  inst.curPos = 0
  inst.endPos = limit

  inst.get = function()
    if inst.curPos < inst.endPos then
      inst.curPos = inst.curPos + 1
      local v = inst.tab[inst.curPos]
      return v
    end
  end
  return inst
end

function Buffer:wrap(tab)
  local inst = Buffer:new(#tab)
  inst.tab = tab
  return inst
end

function Buffer:put(o)
  table.insert(self.tab, o)
  self.curPos = self.curPos+1
  return self
end

function Buffer:pos()
  return self.curPos
end

function Buffer:limit()
  return self.endPos
end

function Buffer:unwind()
  return self.tab, self:pos()
end

