color = {
  -- hue shift in radians
  rotateHue = function(shift, r, g, b)
    local cosa = math.cos(shift);
    local sina = math.sin(shift);
    local neo = {
      cosa + (1 - cosa) / 3,
      (1 - cosa) / 3 - math.sqrt(1 / 3) * sina,
      (1 - cosa) / 3 + math.sqrt(1 / 3) * sina,
    }
    return
      math.clampByte(r * neo[1] + g * neo[2] + b * neo[3]),
      math.clampByte(r * neo[3] + g * neo[1] + b * neo[2]),
      math.clampByte(r * neo[2] + g * neo[3] + b * neo[1])
  end
}
