utils = {
  -- start position is 1-based, endPosition is inclusive
  iterator = function(tab, from, to)
    local i = from or 1
    local e = to or #tab
    return function ()
      if i <= e then
        local v = tab[i]
        i = i + 1
        return v
      end
    end
  end,

  isarray = function(o)
    if type(o) ~= "table" then return false end

    local i = 1
    for k in pairs(o) do
      if k ~= i then return false end
      i = i + 1
    end
    return true
  end,

  copyarray = function(tab)
    local copy = {}
    for i, v in pairs(tab) do
      copy[i] = v
    end
    return copy
  end,

  tostring = function(o, maxTableSize)
    local t = type(o)
    -- trace("" .. tostring(o) .. " is " .. t .. " at level " .. level)
    if t == "table" then
      local arraySize = #o
      if maxTableSize and arraySize > maxTableSize then
        return "#[" .. arraySize .. "]"
      elseif utils.isarray(o) then
        return string.join(", ", o, "[", "]")
      else
        local s = ""
        for k, v in pairs(o) do
          if string.len(s) > 0 then s = s .. ", " end
          s = s .. k .. ": " .. utils.tostring(v, maxTableSize)
        end
        return "{" .. s .. "}"
      end
    else
      return tostring(o)
    end
  end,
}


--[[

Initialize with Cadencer:new, then you should call callEffect on it in the main loop
Simplest form:
  function BOOT()
    cadencer = Cadencer:new(effects)
  end

  function TIC()
    cadencer:callEffect(time())
  end

  function BDR(row)
    cadencer:callBDR(row, time())
  end

* "effects" is a table of effects with those possible props:
  * name (optional) is for tracing purposes. will be generated if not provided
  * duration is in seconds. No duration means neverending
  * context (optional) for effect-specific state/options
  * the init function (optional) is called once when the effect starts and takes the current time and the effect object as argument
  * the func function (required) is called once per tick and takes the current overall time, the time spent since the start of the effect and the effect object as arguments. The effect ends prematurely if it returns true.
  * the bdr function (optional) is called once per scanline and takes the line number, the current overall time, the time spent since the start of the effect, and the effect object as arguments

Example:
{
  {
    name = "effect1",
    duration = 5, -- seconds, none is infinite
    init = myInitFunction1
    func = myEffectFunctionA
  },
  {
    name = "effect2",
    duration = 5700,
    func = myEffectFunction2,
    bdr = myBDRFunction2
  }
}


* startEffect (optional) is the target effect name if you want to start from that effect, thus skipping the previous ones.
* endFunc (optional) is a function to display after all effects have been played. May be your target's system exit() function
* pulse (optional) is inverse of time unit unit. use 1 for seconds, 1000 for milliseconds, 60 for frames, etc

There are 3 time counts:
* overallTime: the time given to callEffect() that's supposed to be the amount of time since the beginning of the timeline.
It can be in any unit you want, as long as you also define pulse (see above)
* liveTime: the actual time taken into account for the effect computation, after removing startTime and pauseTime from overallTime
  * startTime is computed from startEffect: this is the amount of time as though we had started from the beginning of the effects list
  * pauseTime is the amount of time the cadencer has been in a paused state
* effectTime: amount of time since the effect started


Possible optional props:
* startEffect: name of the effect to start with
* endFunc: extra effect when all is finished

* consoleFunc : function to print to the console

* pauseKey: key to pause/resume

* infoKey: key to show/hide frames/time
* infoVisible: whether to show infos
* infoFunc: function to print infos table

]]


Cadencer = {}
Cadencer.__index = Cadencer

function Cadencer:new(effects, props)
  local inst = setmetatable({}, Cadencer)

  inst.pulse = 1
  inst.pauseTime = nil
  inst.pauseOffset = 0

  if props then
    inst.pulse = props.pulse or inst.pulse -- inverse of unit. use 1 for seconds, 1000 for milliseconds, 60 for frames, etc
    inst.startEffect = props.startEffect
    inst.endFunc = props.endFunc

    if props.pauseKey and props.pauseKey > 0 then
      inst.pauseKey = props.pauseKey
    end

    if props.infoKey and props.infoKey > 0 then
      inst.infoKey = props.infoKey
    end
    inst.infoVisible = props.infoVisible
    inst.infoFunc = props.infoFunc or utils.tostring
  end

  -- after props
  inst.effects = effects
  inst:initEffects()

  return inst
end

-- Adds startTime (ms), context, effect start (in offset ms), effect stop (in offset ms)
function Cadencer:initEffects()
  self.startOffset = 0
  local effectOffset = 0

  self:buildSteps()

  for i, effect in ipairs(self.effects) do
    if effect.name == nil then
      effect.name = "effect-" .. string.format("%02d", i)
    end

    if effect.name == self.startEffect then
      self.startOffset = effectOffset
    end

    if not effect.func then
      error("No func defined for effect " .. effect.name)
    end

    if not effect.init then
      effect.init = function() end
    end

    if not effect.context then
      effect.context = {}
    end

    if effect.duration and effect.duration >= 0 then
      effectOffset = effectOffset + effect.duration
    end
  end
end

-- rebuilds timeline in case of timeline change during execution
function Cadencer:buildSteps() -- 1 based
  local from = (self.latestEffect or 0)+1
  local offset = from == 1 and 0 or self.effects[from-1].stop
  for i=from, #self.effects do
    local effect = self.effects[i]
    effect.start = offset
    if effect.duration and effect.duration >= 0 then
      effect.stop = effect.start + effect.duration
      offset = offset + effect.duration
    end
  end
end

function Cadencer:convertUnit(duration)
  return duration/self.pulse
end

function Cadencer:liveTime(timestamp)
  return timestamp + self.startOffset - self.pauseOffset
end

function Cadencer:pickEffect(liveTime)
  for idx = (self.latestEffect or 1), #self.effects do
    local effect = self.effects[idx]
    -- also addressing case where first effect is never ending
    if (not effect.start or liveTime >= effect.start) and (not effect.stop or liveTime < effect.stop) then
      return idx, effect
    end
  end
end

function Cadencer:readyEffect(liveTime)
  local idx, effect = self:pickEffect(liveTime)
  if effect and idx ~= self.latestEffect then
    --trace("Starting effect: " .. effect.name .. " at " .. liveTime)
    effect.init(liveTime, effect)
    self.latestEffect = idx
  end
  return idx, effect
end

function Cadencer:callEffect(overallTime)
  local convertedTime = self:convertUnit(overallTime)
  local paused = self:handlePause(convertedTime)
  if paused then return end

  local liveTime = self:liveTime(convertedTime)
  local idx, effect = self:readyEffect(liveTime)
  if effect then
    local effectTime = liveTime - (effect.start or 0);
    if effect.func(liveTime, effectTime, effect) then -- requested premature ending
      effect.stop = liveTime
      self:buildSteps()
    end
  elseif self.latestEffect and self.endFunc then
    self.endFunc()
  end

  self:handleInfo(liveTime, effect)
end


function Cadencer:callBDR(row, overallTime)
  if self:paused() then return end

  local convertedTime = self:convertUnit(overallTime)
  local liveTime = self:liveTime(convertedTime)
  local _, effect = self:readyEffect(liveTime)
  if effect and effect.bdr then
    local effectTime = liveTime - (effect.start or 0);
    effect.bdr(row, liveTime, effectTime, effect)
  end
end


function Cadencer:handlePause(timestamp)
  local paused = self:paused()
  if self.pauseKey and keyp(self.pauseKey) then
    if paused then
      local pausedTime = self:resume(timestamp)
      self.pauseOffset = self.pauseOffset + pausedTime
    else
      self:pause(timestamp)
    end
    paused = not paused
  end
  return paused -- add more if needed
end

function Cadencer:pause(timestamp)
  if not self:paused() then
    self.pauseTime = timestamp
  end
end

-- returns the pause gap when resuming, else nil
function Cadencer:resume(timestamp)
  if self:paused() then
    local pauseTime = self.pauseTime
    self.pauseTime = nil
    return timestamp - pauseTime
  end
end

function Cadencer:paused()
  return self.pauseTime ~= nil
end


function Cadencer:handleInfo(liveTime, effect)
  if self.infoKey and keyp(self.infoKey) then
    self.infoVisible = not self.infoVisible
  end
  if self.infoVisible and self.infoFunc then
    self.infoFunc(self:info(liveTime, effect))
  end
end

function Cadencer:info(liveTime, effect)
  return {
    e = effect and effect.name or nil,
    t = liveTime,
  }
end

lzw = {
  LZW_MAX_CODES = 4096;
  LZW_MAX_BITS = 12;
  LZW_CODE_MASKS = {
    0x0000,
    0x0001, 0x0003, 0x0007, 0x000F,
    0x001F, 0x003F, 0x007F, 0x00FF,
    0x01FF, 0x03FF, 0x07FF, 0x0FFF,
    0x1FFF, 0x3FFF, 0x7FFF, 0xFFFF
  },


  decode = function(tab, from, totalSize, lzwMinCodeSize)
    local is = Stream:new(tab, from)
    local state = {
      totalSize = totalSize,
      lzwMinCodeSize = lzwMinCodeSize,
      -- curByte, curSize, nbBitsLeft are filled as the algorithm runs
      buffer = Buffer:wrap({})
    }

    -- those shouldn't be bigger than LZW_MAX_CODES
    local dStack, suffix, prefix = {}, {}, {}

    local clearCode = 1 << state.lzwMinCodeSize
    state.curSize = state.lzwMinCodeSize + 1
    local result = Buffer:new(totalSize)
    state.nbBitsLeft = 0

    local stackCount = lzw.LZW_MAX_CODES
    local topSlot = 1 << state.curSize
    local slot = clearCode + 2

    local code, oCode, fCode = lzw.nextCode(is, state), 0, 0
    while code ~= clearCode+1 do
      if code == nil then
        error("code not found")
      end

      if code == clearCode then
        state.curSize = state.lzwMinCodeSize + 1
        slot = clearCode + 2
        topSlot = 1 << state.curSize
        while (code == clearCode) do
          code = lzw.nextCode(is, state)
        end
        if code == clearCode+1 then
          break
        end
        -- if code >= slot then code = 0  -- Shouldn't happen ;)

        oCode = code fCode = code
        if result:limit() > result:pos() then
          result:put(code)
        else
          error("Premature EOF")
        end
      else
        local codeSave = code
        if code >= slot then
          code = oCode
          dStack[stackCount] = fCode
          stackCount = stackCount-1
        end
        while (code >= clearCode + 2) do
          dStack[stackCount] = suffix[code+1]
          stackCount = stackCount-1
          code = prefix[code+1]
        end
        dStack[stackCount] = code
        stackCount = stackCount-1

        fCode = code
        slot = slot+1
        suffix[slot] = fCode
        prefix[slot] = oCode
        oCode = codeSave

        if (slot == topSlot and state.curSize < lzw.LZW_MAX_BITS) then
          topSlot = topSlot << 1
          state.curSize = state.curSize+1
        end

        --if result:pos() + (LZW_MAX_CODES - stackCount) > result:limit() then error("Premature EOF") end

        while (stackCount < lzw.LZW_MAX_CODES) do
          stackCount = stackCount+1
          result:put(dStack[stackCount])
        end
      end
      code = lzw.nextCode(is, state)
    end
    return result.tab
  end,


  readBuffer = function(is)
    local availableBytes = is:read()
    if availableBytes == nil then
      return nil
    else
      local lzwBuffer = is:readN(availableBytes)
      if #lzwBuffer ~= availableBytes then
        error("Expected " .. availableBytes .. " bytes, but only read " .. #lzwBuffer)
      else
        return Buffer:wrap(lzwBuffer)
      end
    end
  end,


  nextCode = function(is, state)
    local result

    if state.nbBitsLeft == 0 then
      if state.buffer:limit() == state.buffer:pos() then
        state.buffer = lzw.readBuffer(is)
        if state.buffer == nil then return nil end
      end
      state.curByte = state.buffer:get()
      result = state.curByte
      state.nbBitsLeft = 8
    else
      result = state.curByte >> (8 - state.nbBitsLeft)
    end

    while state.curSize > state.nbBitsLeft do
      if state.buffer:limit() == state.buffer:pos() then
        state.buffer = lzw.readBuffer(is)
        if state.buffer == nil then return nil end
      end
      state.curByte = state.buffer:get()
      result = result | (state.curByte << state.nbBitsLeft)
      state.nbBitsLeft = state.nbBitsLeft + 8
    end
    state.nbBitsLeft = state.nbBitsLeft - state.curSize
    result = result & lzw.LZW_CODE_MASKS[state.curSize+1]
    return result
  end
}

Vec2 = {}
Vec2.__index = Vec2

function Vec2:new(x, y)
  local inst = setmetatable({}, Vec2)
  inst.x = x
  inst.y = y
  return inst
end

function Vec2:clone()
  return Vec2:new(self.x, self.y)
end

function Vec2:trans(dx, dy)
  self.x = self.x + dx
  self.y = self.y + dy
  return self
end

function Vec2:transV(vec)
  return self:trans(vec.x, vec.y)
end

function Vec2:mul(scalarX, scalarY)
  self.x = self.x*scalarX
  self.y = self.y*scalarY
  return self
end

function Vec2:neg()
  self.x = -self.x
  self.y = -self.y
  return self
end

function Vec2:rotate(angle)
  local c = math.cos(angle)
  local s = math.sin(angle)
  self.x, self.y = c*self.x - s*self.y, s*self.x + c*self.y
  return self
end

function Vec2:dist(vec)
  return math.sqrt((self.x-vec.x)^2 + (self.y-vec.y)^2)
end

function Vec2:unwind()
  return self.x, self.y
end


