Vec3 = {}
Vec3.__index = Vec3

function Vec3:new(x, y, z)
  local inst = setmetatable({}, Vec3)
  inst.x = x
  inst.y = y
  inst.z = z
  return inst
end

function Vec3:clone()
  return Vec3:new(self.x, self.y, self.z)
end

function Vec3:trans(dx, dy, dz)
  self.x = self.x + dx
  self.y = self.y + dy
  self.z = self.z + dz
  return self
end

function Vec3:transV(vec)
  return self:trans(vec.x, vec.y, vec.z)
end

function Vec3:mul(scalarX, scalarY, scalarZ)
  self.x = self.x*scalarX
  self.y = self.y*scalarY
  self.z = self.z*scalarZ
  return self
end

function Vec3:rotateX(angle)
  local c = math.cos(angle)
  local s = math.sin(angle)
  self.y, self.z = s*self.z - c*self.y, s*self.y + c*self.z
  return self
end

function Vec3:rotateY(angle)
  local c = math.cos(angle)
  local s = math.sin(angle)
  self.x, self.z = s*self.z + c*self.x, s*self.x - c*self.z
  return self
end

function Vec3:rotateZ(angle)
  local c = math.cos(angle)
  local s = math.sin(angle)
  self.x, self.y = s*self.x + c*self.y, s*self.y - c*self.x
  return self
end

function Vec3:rotate(angleX, angleY, angleZ)
  return self:rotateX(angleX):rotateY(angleY):rotateZ(angleZ)
end

function Vec3:dist(vec)
  return math.sqrt((self.x-vec.x)^2 + (self.y-vec.y)^2 + (self.z-vec.z)^2)
end

function Vec3:unwind()
  return self.x, self.y, self.z
end


bezier = {
  --[[
    https://javascript.info/bezier-curve
    V = (1−t)³v1 + 3(1−t)²tv2 +3(1−t)t²v3 + t³v4
    with t between [0, 1]
  ]]
  position = function(v1, v2, v3, v4, t)
    local x = 1 - t
    local x1 = x ^ 3
    local x2 = 3 * x ^ 2 * t
    local x3 = 3 * x * t ^ 2
    local x4 = t ^ 3

    return Vec2:new(
        x1 * v1.x + x2 * v2.x + x3 * v3.x + x4 * v4.x,
        x1 * v1.y + x2 * v2.y + x3 * v3.y + x4 * v4.y
    )
  end,

  -- approx. see https://stackoverflow.com/a/37862545/6693204
  lengthApprox = function(v1, v2, v3, v4)
    local chordLen = v1:dist(v4)
    local controlLen = v1:dist(v2) + v2:dist(v3) + v3:dist(v4)
    return (controlLen + chordLen) / 2;
  end,
}

