    local tileW, tileH = w*8*math.floor(scale), h*8*math.floor(scale)
    local rows, cols = dh//tileW, dw//tileH -- too bad if dw or dh aren't a multiple of 8*w*scale or 8*h*scale
    local dy = y
    for r = 1, rows do
      local dx = x
      for c = 1, cols do
