  printInfos = function(table, col, right, bottom)
    local cols = 0
    local rows = 0
    local printable = {}

    for k, v in pairs(table) do
      local sv = type(v) == "number" and string.format("%.2f", v) or tostring(v)
      printable[k] = sv
      local len = string.len(sv)
      if len > cols then
        cols = len
      end
      rows = rows + 1
    end

    local xi = 5
    local yi = 6
    local x = right and tic.width - xi * cols or 2
    local y = bottom and tic.height - yi * rows or 2

    for k, v in pairs(printable) do
      print(k .. ": " .. v, x, y, col, false, 1, true)
      y = y + yi
    end
