  -- same as the original but with float scaling
  -- TODO impl fixed param
  print = function(text, x, y, col, fixed, scale)
    scale = scale or 1
    local fontSizeX, fontSizeY = 5, 6
    local drawSizeX, drawSizeY = fontSizeX * scale,fontSizeY * scale
    local drawBoundX, drawBoundY = math.round(drawSizeX)-1, math.round(drawSizeY)-1
    local drawSpacedX, drawSpacedY = (fontSizeX+1) * scale, (fontSizeY+1) * scale

    for line in string.gmatch(text, "([^\r\n]+)") do
      local lx = x
