        local char = string.sub(line, i, i)
        local charNum = string.byte(char)
        if charNum <= 127 then
          local charBytes = tic.peekChar(charNum)
          -- let's interpolate
          for cy = 0,  drawBoundY do
            local ty = y+cy
            if ty >= 0 and ty < tic.height then
              local fy = math.floor(fontSizeY * cy / drawSizeY)
              local clb = charBytes[fy+1]
              for cx = 0, drawBoundX do
                local tx = lx+cx
                if tx >= 0 and tx < tic.width then
                  local fx = math.floor(fontSizeX * cx / drawSizeX)
                  if (clb & (1 << fx)) ~=0 then
                    pix(tx, ty, col)
                  end
                end
              end
            end
          end
        end
        lx = lx + drawSpacedX
      end
      y = y + drawSpacedY
    end
  end
}

gif = {
  -- returns data, colors, width, height
  read = function(bytes)
    local is = Stream:new(bytes)
    local colors = gif.readColorData(is)
    local width, height
    local data

    local finished
    while not finished do
      local type = is:read()
      if type == 0x21 then -- '!'
        gif.skipExtBlock(is)
      elseif type == 0x2C then -- ','
        width, height = gif.readDimension(is)
        data = gif.readImageData(is, width, height)
        finished = true
      elseif type == 0x3B then -- ';'
        finished = true
      else
        error("unknown block: " .. tostring(type))
      end
    end
    return data, colors, width, height
  end,

  readColorData = function(is)
    local header = is:readN(13)
    if (header[11] & 0x80) == 0 then
      error("Bad color header")
    end

    local colorCount = 1 << ((header[11] & 0x0F) + 1)
    return is:readN(3 * colorCount)
  end,

  -- returns width, height
  readDimension = function(is)
    local buffer = is:readN(9)
    if (buffer[9] & 0x40) ~= 0 then -- interlaced format ?
      error("Unsupported format")
    end

    local width = buffer[5] | (buffer[6] << 8)
    local height = buffer[7] | (buffer[8] << 8)
    if width <= 0 or width >= 65536 or height <= 0 or height >= 65536 then
      error("Image size: " .. width .. "," .. height)
    end

    if (buffer[9] & 0x80) ~= 0 then
      error("Local color table")
    end
    return width, height
  end,

  -- returns image data, updated cursor
  readImageData = function(is, width, height)
    local lzwMinCodeSize = is:read()
    if lzwMinCodeSize < 2 or lzwMinCodeSize > 9 then
      error("LZW min code size: " + lzwMinCodeSize)
    end
    local tab, pos = is:unwind();
    return lzw.decode(tab, pos, width * height, lzwMinCodeSize)
  end,

  skipExtBlock = function(is)
    is:skipN(1) -- id
    while true do
      local size = is:read() or 0
      if size == 0 then break end
      is:skipN(size)
    end
  end,
}
