intro2Background = Image:new(gif.read(intro2Gif))

function intro2Init(lt, fx)
  local cx = fx.context
  cx.background = intro2Background

  cx.twinklingCols = {1, 2, 3, 11, 14, 15}

  cx.fadeIn = 300
  cx.scalePalette = function(scale)
    scale = math.max(0, scale)
    result = {}
    for _,comp in ipairs(fx.context.background.palette) do
      table.insert(result, comp * scale)
    end
    return result
  end

  cls() -- not to see the last frame of the previous effect
  tic.pokePalette(fx.context.background.palette)
  tic.pokeBorderColor(10)
end

function intro2Func(lt, et, fx)
  local cx = fx.context

  -- palette fading and twinkling
  local pal
  if et < cx.fadeIn then
    pal = cx.scalePalette(et/cx.fadeIn)
  else
    pal = utils.copyarray(fx.context.background.palette)
  end

  local scale = 0.9 + (1 + math.sin(math.twopi * et/150))*(0.05/2)
  for _, c in ipairs(cx.twinklingCols) do
    local off = 1+c*3
    pal[off] = pal[off] * scale
    pal[off+1] = pal[off+1] * scale
    pal[off+2] = pal[off+2] * scale
  end
  tic.pokePalette(pal)

  cx.background:draw(0, 0, function(x, y, col) pix(x, y, col) end)
end


intro2Effect = {name= "intro2", duration = 6000, init = intro2Init, func = intro2Func }


promptImage = Image:new(gif.read(promptGif))

function promptInit(lt, fx)
  tic.syncMemCopy(0, 5, tic.ram.sfxState, tic.ram.persistentMem-1)

  local cx = {
    prompt = promptImage,
    fadeInDuration = 2000, -- duration
    backColor = 15,
    transparencyCol = 2,
    
    vblDim = 0.95,
    vblThick = 8*4,
    vblSpeed = 3000, -- time to cross the screen
    vblY = 30,

    decrunchPalette = "1a1c2c5d275db13e53ef7d57ffcd75a7f07038b76425717929366f3b5dc941a6f673eff7f4f4f4",
    decrunchCol = 2, -- color used for bdr, also the transparent color to the prompts
    decrunchMinCol = 1,
    decrunchMaxCol = 12,
    decrunchProba = 0.6, -- probability of color change
  }

  cx.pickDecrunchCol = function()
    local col = math.floor(cx.decrunchMinCol+math.random()*cx.decrunchMaxCol)
    local offSet = 2*col+1
    local r = tonumber(string.sub(cx.decrunchPalette, offSet, offSet+1), 16)
    local g = tonumber(string.sub(cx.decrunchPalette, offSet+2, offSet+3), 16)
    local b = tonumber(string.sub(cx.decrunchPalette, offSet+4, offSet+5), 16)
    return r, g, b
  end

  cx.pickVblDecrunchColor = function(vblZone)
    local ratio = vblZone and cx.vblDim or 1
    local r, g, b = cx.pickDecrunchCol()
    return r*ratio, g*ratio, b*ratio
  end

  cx.scalePalette = function(palette, scale)
    scale = math.max(0, scale)
    result = {}
    for _,comp in ipairs(palette) do
      table.insert(result, comp * scale)
    end
    return result
  end

  -- timings
  cx.worldTime = cx.fadeInDuration
  cx.dotsTime = cx.worldTime + 300
  cx.prismTime = cx.dotsTime + 600
  cx.readyTime = cx.prismTime + 330
  cx.time2Time = cx.readyTime + 300
  cx.execTime = cx.time2Time + 530
  cx.decrunchTime = cx.execTime + 600
  --cx.copperIn = cx.decrunchTime + 600
  --cx.copperFix = cx.copperIn + 400
  --cx.fxEnd = cx.copperFix + 2000
  cx.fxEnd = cx.decrunchTime + 2000
  fx.stop = fx.start + cx.fxEnd
  cadencer:buildSteps()

  cx.vblPalette = {}
  for _,comp in ipairs(promptImage.palette) do
    table.insert(cx.vblPalette, comp  * cx.vblDim)
  end

  cx.stdPalLive = cx.prompt.palette
