  cx.vblY = math.floor(tic.height * lt / cx.vblSpeed) % tic.height

  if et < cx.decrunchTime then
    if et < cx.fadeInDuration then
      local fadePalette = {}
      local ratio = et/2000
      for _,comp in ipairs(linePalette) do
        table.insert(fadePalette, comp*ratio)
      end
      linePalette = fadePalette
    end
    tic.pokePalette(linePalette)
  else -- decrunch
    if math.random() > cx.decrunchProba then
      local r,g, b = cx.pickVblDecrunchColor(vblZone)
      tic.pokeColor(cx.decrunchCol, r, g, b)
    end
  end
end

function promptFunc(lt, et, fx)
