    --[[
    local nbColors = cx.prompt:nbColors()
    local palette = {}
    if et < cx.fadeInDuration then
      for i=0,nbColors-1 do
        local ratio = et/2000
        local r, g, b = cx.prompt:color(i)
        table.insert(palette, r*ratio)
        table.insert(palette, g*ratio)
        table.insert(palette, b*ratio)
      end
    else
