    spr(112, promptLeft1, 56,  cx.transparencyCol, 1, 0, 0, 16, 2)
    spr(229, promptLeft2, 64,  cx.transparencyCol, 1, 0, 0, 9, 1)
  end
  -- 100% + Ready
  if et > cx.readyTime then
    spr(144, promptLeft1, 72,  cx.transparencyCol, 1, 0, 0, 14, 1)
    spr(160, promptLeft1, 96,  cx.transparencyCol, 1, 0, 0, 4, 1)
  end
  -- timeline2.exe
  if et > cx.time2Time then
    spr(176, promptLeft1, 112, cx.transparencyCol, 1, 0, 0, 8, 1)
  end
  -- Execution
  if et > cx.execTime then
    spr(192, promptLeft1, 120, cx.transparencyCol, 1, 0, 0, 16, 1)
    spr(245, promptLeft2, 120, cx.transparencyCol, 1, 0, 0, 8, 1)
  end
end


