-- bump 5 pix during 0.5sec each 2sec
function scrollBumpY(et, cx)
  local cycleStep = (et+cx.bumpDuration)%cx.bumpCycle
  if cycleStep < cx.bumpDuration then
    return cx.bumpAmp * math.sin(math.pi * cycleStep /cx.bumpDuration)
  else
    return 0
  end
end

function scrollDraw(line, y, et, delay, speedX, cx, font)
  local text = cx.text[line]
  if (cx.textIndex[line] or 0) <= string.len(text) then
    local xBase = tic.width-(et-delay)//speedX
    --trace("xBase: " .. xBase)
    local totalTextSpan = cx.textSpan(xBase, font) -- from the beginning of the text
    -- trace("totalTextSpan: " .. totalTextSpan)
    local x
    if xBase < 0 then
      x = math.fmod(xBase, font.charAndSpaceWidth)
    else
      x = xBase
    end

    local extraChars = cx.xShiftChars
    local visibleSpan = cx.textSpan(x, font)
    cx.textIndex[line] = totalTextSpan - visibleSpan

    -- going backwards to be able to display chars when the sine oscillates to the right and shows "previous" chars (or to the left and the "next" ones)
    local regress = math.min(cx.textIndex[line]-1, extraChars)
    local index = cx.textIndex[line] - regress
    x = x - regress * font.charAndSpaceWidth
    visibleSpan = math.max(0, regress + visibleSpan + extraChars)
    local visibleText = string.sub(text, index, index + visibleSpan)
    --trace("xBase: " .. xBase .. ", x: " .. x ..", totalTextSpan: " .. totalTextSpan .. ", visibleSpan: " .. visibleSpan .. ", textIndex: " .. cx.textIndex[line] .. ", text: " .. visibleText)
    scrollDrawChars(x, y, visibleText, et, delay, cx, font)
  end
end

function scrollDrawChars(x, y, text, et, delay, cx, font)
  for i=1, #text do
    scrollDrawChar(x, y, string.sub(text, i, i), et, delay, cx, font)
    x = x + font.charAndSpaceWidth
  end
end

function scrollDrawChar(x, y, char, et, delay, cx, font)
  local charNum = string.byte(char) - font.offset
  if charNum < 0 then return end

  local charX = charNum % font.cols
  local charY = charNum // font.cols

  local fontX = charX * font.charWidth
  local fontY = charY * font.fontWidth * font.charHeight

  for py=y, y+font.charHeight-1 do
    local xShift = cx.xShiftAmp * math.sin(math.twopi * py/tic.height + math.twopi * et / cx.oscX)
    for i=0, font.charWidth-1 do
      local px = x + xShift + i
      local col =  font.image:pix(fontX + i, fontY)
      if col ~= cx.transparentCol and px >=0 then
        pix(px, py, col)
      end
    end
    fontY = fontY + 1
  end
end

function scrollBackground(lt, et, fx)
