function scrollLogo(lt, et, fx)
  local cx = fx.context
  if et >= cx.logoIn then
    local pixFunc = function(x, y, col) pix(x, y, col) end
    local transpFunc = function(x, y, col) return col ~= 0 end

    local lx, ly = (tic.width - cx.logo.width)/2, (tic.height - cx.logo.height)/2
    local timeLeft = fx.stop - lt
    if (et >= cx.glitchOut1 and et < cx.glitchIn2) or (et >= cx.glitchOut2 and timeLeft > cx.transitionOut) then
      cx.logo:draw(lx, ly, pixFunc, transpFunc)
    else -- Glitching. Really buggy as zones overlap, but it works good enough!
      if tic.millisToFrames(et)%5 == 0 then
        cx.glitches = {}
        local y0 = 0
        for i = 1, cx.glitchPasses do
          local y1 = math.random(y0, cx.logo.height-1)
          local y2 = math.random(y1, cx.logo.height-1)
          local xOff = math.random(-cx.glitchMaxOff, cx.glitchMaxOff)
          table.insert(cx.glitches, {y1, y2, xOff})
          --y0 = y2
        end
      end

      local sy = 0
      for i, g in ipairs(cx.glitches) do
        cx.logo:drawRect(0, sy, cx.logo.width, g[1]-sy, lx, ly+sy, pixFunc, transpFunc)
        cx.logo:drawRect(0, g[1], cx.logo.width, g[2]-g[1]+1, lx+g[3], ly+g[1], pixFunc, transpFunc)
        sy = g[2]
      end
      cx.logo:drawRect(0, sy, cx.logo.width, cx.logo.height-sy, lx, ly+sy, pixFunc, transpFunc)
    end
  end
end


