function dragonBallStars(lt, et, fx)
  --cls()
  local cx = fx.context

  local lfr = (cx.ballAirDuration + cx.ballWarpRotFactor*cx.ballWarpDuration)/20 -- ball moves slower when on the ground
  cx.ballAngleX = lfr * 0.018 -- ~same as glenz, too tired
  cx.ballAngleY = lfr * - 0.037
  cx.ballAngleZ = lfr * 0.010

  local stars = {}

  local starColFront, starColBack = 12, 13
  local sx, sy = cx.ballCenterX, cx.ballCenterY
  local starSize = cx.starSize * cx.zoomFactor
  for _, r in ipairs(cx.starShifts) do
    local rotX, rotY, rotZ = table.unpack(r)
    local tris = {}
    local z
    for _, t in ipairs(cx.starTriangles) do
      local v1, v2, v3 = cx.starVertex[t[1]], cx.starVertex[t[2]], cx.starVertex[t[3]]
      v1 = v1:clone():mul(starSize, starSize, 1):rotate(rotX, rotY, rotZ):rotate(cx.ballAngleX, cx.ballAngleY, cx.ballAngleZ)
      v2 = v2:clone():mul(starSize, starSize, 1):rotate(rotX, rotY, rotZ):rotate(cx.ballAngleX, cx.ballAngleY, cx.ballAngleZ)
      v3 = v3:clone():mul(starSize, starSize, 1):rotate(rotX, rotY, rotZ):rotate(cx.ballAngleX, cx.ballAngleY, cx.ballAngleZ)
      z = v1.z
      table.insert(tris,{sx+v1.x, sy+v1.y*cx.warpFactor, sx+v2.x, sy+v2.y*cx.warpFactor, sx+v3.x, sy+v3.y*cx.warpFactor})
    end
    table.insert(stars,  {z, tris})
  end
  -- ordering stars so those in the back don't overlap those in the front
  table.sort(stars, function(s1, s2) return s1[1] < s2[1] end)

  for i, s in ipairs(stars) do
    local starCol = s[1] < 0 and starColBack or starColFront
    for j, t in ipairs(s[2]) do
      table.insert(t, starCol)
      tri(table.unpack(t))
    end
  end
end

function dragonBallDraw(lt, et, fx)
  local cx = fx.context

  local ballAmpFactor = math.sin(math.twopi * et/cx.ballBounceDuration)
  if et < fx.duration - cx.ballOut then -- cx.ballBottomY isn't updating anymore during transition
    cx.ballBottomY = cx.ballBounceOriginY + cx.ballBounceAmp * ballAmpFactor + cx.ballRadius
    if cx.ballBottomY > cx.groundY then -- on the ground
      cx.warpFactor = cx.ballWarpMinFactor+(cx.ballWarpNomFactor-cx.ballWarpMinFactor)*(1-(cx.ballBottomY-cx.groundY)/cx.ballWarpRange)
      cx.ballBottomY = cx.groundY

      if not cx.ballWarpStart then -- starting warp
        cx.ballWarpStart = et
        cx.ballAirStart = nil
        cx.ballWarpRef = cx.ballWarpDuration
        cx.ballWallWarpRef = cx.ballWallWarpDelay
      else
        cx.ballWarpDuration = cx.ballWarpRef + (et - cx.ballWarpStart)
        cx.ballWallWarpDelay = cx.ballWallWarpRef + (et - cx.ballWarpStart)
      end
    else -- in the air
      if ballAmpFactor < -0.95 then cx.ballGoingUp = false end

      if cx.ballGoingUp then
        local r1 = 1-(cx.groundY - cx.ballBottomY)/(cx.groundY - (cx.ballBounceOriginY - cx.ballBounceAmp + cx.ballRadius))
        local r2 = (1+math.sin(2 * math.twopi * r1))/2
        cx.warpFactor = cx.ballWarpNomFactor - (cx.ballWarpNomFactor - cx.ballWarpMinFactor)/2 * r2 * r1
      else
        cx.warpFactor = cx.ballWarpNomFactor
      end

      if cx.ballWarpStart then -- just out of warping
        cx.ballGoingUp = true
        cx.ballAirStart = et
        cx.ballWarpStart = nil
        cx.ballAirRef = cx.ballAirDuration
      else
        cx.ballAirDuration = cx.ballAirRef + et - cx.ballAirStart
      end
    end
  end

  -- bouncing on walls
  cx.ballCenterX = cx.ballWallX + (et-cx.ballWallStart - cx.ballWallWarpDelay*cx.ballWarpTransFactor)/cx.ballSpeedX
  --cx.ballCenterX = cx.ballWallX + (et - (cx.ballAirStart or et) + et - (cx.ballWarpStart or et))/cx.ballSpeedX
  -- bouncing on walls
  if cx.bounceEnabled then
    if cx.ballCenterX - cx.ballRadius < 0 then
      cx.ballSpeedX = -cx.ballSpeedX
      cx.ballWallX = cx.ballRadius
      cx.ballWallWarpRef = -(et - (cx.ballWarpStart or 0))
      cx.ballWallStart = et
      cx.ballWallWarpDelay = 0
    end
  end
  if cx.ballCenterX + cx.ballRadius >= tic.width then
    cx.ballSpeedX = -cx.ballSpeedX
    cx.ballWallX = tic.width - cx.ballRadius
    cx.ballWallWarpRef = -(et - (cx.ballWarpStart or 0))
    cx.ballWallStart = et
    cx.ballWallWarpDelay = 0
    cx.bounceEnabled = true
  end

  cx.ballCenterY = cx.ballBottomY - cx.ballRadius * cx.warpFactor

  local ballRadius = cx.ballRadius * cx.zoomFactor
  for x = 0, ballRadius-1 do
    local angle = math.acos(x/cx.ballRadius)
    local y = ballRadius * math.sin(angle) * cx.warpFactor

    local cLeft, cRight = cx.ballCenterX - x, cx.ballCenterX + x
    local cTop, cBottom = cx.ballCenterY - y, cx.ballCenterY + y

    -- color map
    local horizons = {
      cx.ballHz1,
      cx.ballHz2,
      cx.ballHz3,
      cx.ballHz4,
      cx.ballHz5,
      cx.ballHz6,
      cBottom-1
    }
    local sections = {}
    local prevHz = cTop --+1
    for i, h in ipairs(horizons) do
      if h > prevHz then
        if h < cBottom then
          table.insert(sections, { i, prevHz, h, cx.glassColLink[i], i < #horizons})
          prevHz = h
        else
          table.insert(sections, { i, prevHz, cBottom, cx.glassColLink[i], false})
          break
        end
      end
    end

    for _, s in ipairs(sections) do
      local i, hTop, hBottom, col, extraPix = table.unpack(s)
      line(cLeft, hTop, cLeft,hBottom, col)
      line(cRight, hTop, cRight, hBottom, col)
      if extraPix then
        local extraPixCol = cx.glassColLink[i+1]
        pix(cLeft, hBottom-2, extraPixCol)
        pix(cRight, hBottom-2, extraPixCol)
      end
    end

    -- surrounding
    --pix(cLeft, cTop, 10)
    pix(cLeft, cBottom, 7)
    --pix(cRight, cTop, 10)
    pix(cRight, cBottom, 7)
  end
end

