  cx.bzp1 = Vec2:new(0, 0)
  cx.bzp4 = Vec2:new(bzGridSize, -8)
  cx.bzc = Vec2:new((cx.bzp1.x + cx.bzp4.x)/2, (cx.bzp1.y + cx.bzp4.y)/2) -- center

  -- moving points
  local bz2MinX = 2
  local bz2MaxX = 9
  cx.bz2AmpX = (bz2MaxX - bz2MinX)/2
  local bz2MinY = -14
  local bz2MaxY = 16
  cx.bz2AmpY = (bz2MaxY - bz2MinY)/4

  local bz3MinX = bzGridSize - bz2MinX
  local bz3MaxX = bzGridSize - bz2MaxX
  cx.bz3AmpX = (bz3MaxX - bz3MinX)/2
  local bz3MinY = bz2MinY
  local bz3MaxY = bz2MaxY
  cx.bz3AmpY = (bz3MaxY - bz3MinY)/2

  cx.bzAmpY = (bezier.position(cx.bzp1, Vec2:new(bz2MaxX, bz2MaxY), Vec2:new(bz3MaxX, bz3MaxY), cx.bzp4, 0.5).y
             - bezier.position(cx.bzp1, Vec2:new(bz2MinX, bz2MinY), Vec2:new(bz3MinX, bz3MinY), cx.bzp4, 0.5).y)/2

  cx.palMorphIn = 1000
  cx.palMorphOut = 5000
  cx.transitionOut = 800

  cx.text = "                    SL3TP tunnel stable - Proxy 2 transmitting encrypted deltas to GREETZ vault server : Spectrals - Bomb - Exist - Mutant Inc - Alcatraz - Haujobb - Maniacs of Noise - Tpolm - Calodox - Melon - CNCD - FLT - TBL - TRSI - Orange - Lemon - Logoff."

  cx.morphPalette = function(ratio)
    local palette = {}
    for i, c1 in ipairs(cx.fromPalette) do
      local c2 = cx.toPalette[i]
      table.insert(palette, c1 + (c2-c1)*ratio)
    end
    return palette
  end
end



function tunnelFunc(lt, et, fx)
  local cx = fx.context
  --cls()

  -- palette morph
  local palette
  if et < cx.palMorphIn then
    palette = cx.fromPalette
  elseif et < cx.palMorphOut then
    palette = cx.morphPalette((et-cx.palMorphIn)/(cx.palMorphOut - cx.palMorphIn))
  else
    palette = cx.toPalette
  end
  tic.pokePalette(palette)


  -- tile selection
  local ez = et+600
  local secs = ez//1000
  if secs%3 == 0 then
    local diff = ez-1000*secs
    if diff < 300 then
      cx.tile = cx.tiles[2]
    elseif diff < 500 then
      cx.tile = cx.tiles[3]
    elseif diff < 700 then
      cx.tile = cx.tiles[2]
    else
      cx.tile = cx.tiles[1]
    end
  end

  -- warping
  local crankshaft = math.twopi * (et / cx.circleTime) -- circling in circleTime
  local sin = math.sin(crankshaft) -- [-1, 1]

  local bzp2 = Vec2:new(cx.bz2AmpX * sin, cx.bz2AmpY * sin)
  local bzp3 = Vec2:new(cx.bz3AmpX * sin, cx.bz3AmpY * sin)

  for y = 0, tic.height - 1 do
    local tY = y/tic.height

    local bzPt = bezier.position(cx.bzp1, bzp2, bzp3, cx.bzp4, tY):trans(-cx.bzc.x, -cx.bzc.y)
    local zoomFactor = cx.zoomFactorBase^(bzPt.y/cx.bzAmpY) -- depends on the line

    local texLenY = (10 / 1.5^sin) * cx.sprSizeY
    local texGrowY = texLenY / zoomFactor
    local varY = sin * 20
    local pY = math.floor(texGrowY * (tY-0.5) + varY) % cx.sprSizeY -- should be roundInt

    for x = 0, tic.width - 1 do
      local tX = x/tic.width - 0.5
      local texGrowX = texGrowY * tic.ratio -- making sprite square
      local varX = et*.05
      local pX = math.floor(texGrowX * tX + varX) % cx.sprSizeX  -- should be roundInt

      -- local col = tic.peekTileCol(pX, pY) -- too slow, sorry
      local col = cx.tile:pix(pX, pY)
      pix(x, y, col)
    end
  end

  -- scroll
  local zoomFactor = 5.5 + 2 * math.sin(lt/1000) + 3 * math.cos(-lt/617) -- [0, +6]
  local speed = et/12
  local midChar = speed / 6 -- identify which char is supposed to be in the middle of the screen when not zooming
  local charSize = 6*zoomFactor


  -- trying not to print what's out of screen
  local displayed = 1 + math.roundInt(tic.width/charSize)  -- how many chars are displayed on screen
  local x = tic.centerX - midChar * charSize
  local ti = 0
  if x < 0 then
    ti = -x//charSize
    x = x + ti * charSize
  end
  local y = tic.centerY - 3*zoomFactor + 10 * math.sin(et/800)
  local text = string.sub(cx.text, ti, ti+displayed)
  tic.print(text, x, y, 1, false, zoomFactor)


  -- waves transition
  local timeLeft = fx.stop - lt
  if timeLeft < cx.transitionOut then
    y = tic.height*(cx.transitionOut-timeLeft)/cx.transitionOut
    for x = 0, tic.width-1 do
      local xfrac = twopi * x/tic.width
      local amp = 10 + 10 * math.sin(xfrac + lt/1000) + 20 * math.cos(2*xfrac/3 - lt/617)
      line(x, 0, x, y+amp, 5)
    end
  end
end

