function starzInit()
  local cx = {
    farP = 256,
    nearP = 8,
    focal = 150,
    depth = 256-8,
    distance = 0,

    speed = 80,
    centerX = 0.4,
    centerY = 0.5,

    size = 0.5,
    colorIntensity = 3,
    radFactor = 0.3,
    opacity = 0.5,
    white = 5,
    colSpan = 5,

    count = 1024
  }

  local starz = {}
  for i = 1, cx.count do
    table.insert(starz,
    -- stars (x,y) are [-0.5, 0.5]
        Star:new(
            (math.random() - 0.5),
            (math.random() - 0.5),
            8 + i * cx.depth / cx.count
        )
    )
  end
  cx.starz = starz

  -- 1-based
  cx.starDist = function(i)
    local d = cx.distance % cx.depth
    if d < 0 then d=d+cx.depth end
    local n = math.floor(cx.count * d/cx.depth) - i+1
    if n < 1 then n=n+cx.count end
    if n > cx.count then n=n-cx.count end
    return n
  end

  cx.starPos = function(star)
    local z = star.z - cx.distance % cx.depth
    if z < cx.nearP then
      z = z+cx.depth
    elseif z > cx.farP then
      z = z-cx.depth
    end
    return star.x, star.y, z
  end

  return cx
end




-- inspired by http://lol.pm/starfield/?step=6&x=26&y=-10&ox=-211&oy=-33&distance=204&overlay=true
function starzDraw(et, cx)
