    local x,y,z = cx.starPos(cx.starz[cx.starDist(i)])
    local r = cx.focal / z
    local w = z - 8
    local c = (1-(w/cx.depth)^3) * (cx.colSpan+1)
    local angle = math.twopi*et/10000
    local pt = Vec2:new(x * r, y * r):rotate(angle)

    circ((pt.x+cx.centerX)*tic.width, (pt.y+cx.centerY)*tic.height, r * cx.radFactor, c)
  end
  cx.distance = cx.distance + 1/60 * cx.speed-- TODO time since last image actually
end



