-- https://www.shadertoy.com/view/sl3fz4
function glenzDraw(et, cx, ratio, spin, offX, offY, lightCol, darkCol, whiteCol, zThresh)

  local dist = 1 -- distance
  local lfr = spin+et/50 -- TODO + uv.y*29. -- + uv.x*10. -- to make it bend

  local angleX = lfr * 0.018
  local angleY = lfr * -0.037
  local angleZ = lfr * 0.01

  local shiftX = math.sin(et*0.0021)*0.07
  local shiftY = math.sin(-et*0.003)*0.062

  local rotatedVertex = {} -- vec2 list
  local ztable = {} -- scalars

  -- rotate & perspective calc
  for _, vertex in ipairs(cx.vertex) do
    vertex = vertex:clone()
                   :mul(ratio, ratio, ratio) -- change to zoom in/out
                   :rotate(angleX, angleY, angleZ)

    local shiftDist = (1 + vertex.z/dist)
    vertex.x = vertex.x / shiftDist
    vertex.y = vertex.y / shiftDist

    table.insert(rotatedVertex, Vec2:new(shiftX + vertex.x, shiftY + vertex.y))
    table.insert(ztable, vertex.z)
  end

  -- back
  for t = 1, 24 do
    if ztable[cx.sk3[t]] > zThresh then
      local v1 = rotatedVertex[cx.sk1[t]]
      local v2 = rotatedVertex[cx.sk2[t]]
      local v3 = rotatedVertex[cx.sk3[t]]

      local col = (t-1) & 0x2 == 0 and lightCol or darkCol
      tri(cx.pixX(v1.x, offX), cx.pixY(v1.y, offY), cx.pixX(v2.x, offX), cx.pixY(v2.y, offY), cx.pixX(v3.x, offX), cx.pixY(v3.y, offY), col)
    end
  end

  -- front
  for t = 1, 24, 1 do
    if ztable[cx.sk3[t]] <= zThresh then
      local v1 = rotatedVertex[cx.sk1[t]]
      local v2 = rotatedVertex[cx.sk2[t]]
      local v3 = rotatedVertex[cx.sk3[t]]

      if (t-1) & 0x2 == 0 then
        tri(cx.pixX(v1.x, offX), cx.pixY(v1.y, offY), cx.pixX(v2.x, offX), cx.pixY(v2.y, offY), cx.pixX(v3.x, offX), cx.pixY(v3.y, offY), whiteCol)
      end
    end
  end
end


