function starzGlenzFunc(lt, et, fx)
  cls()

  local targetFocal = 150
  local focalDuration = 1500

  local cxs = fx.context.starz
  if(et < focalDuration) then
    cxs.focal = targetFocal * et/focalDuration
  else
    cxs.focal = targetFocal
  end
  starzDraw(et, fx.context.starz)


  local cxg = fx.context.glenz
  local offSetOut = 200
  local rVisible, gVisible, bVisible, yVisible
  local rOffX, gOffX, bOffX, yOffX
  local rOffStable = -83
  local gOffStable = 0
  local bOffStable = 83

  if et >= cxg.greenStart and et < cxg.mergeIn then
    if et >= cxg.greenFix then
      gOffX = gOffStable
    else
      gOffX = gOffStable + offSetOut * (1-(et - cxg.greenStart)/(cxg.greenFix - cxg.greenStart))
    end
    gVisible = true
  end

  if et >= cxg.redStart and et < cxg.mergeIn then
    if et >= cxg.redFix then
      rOffX = rOffStable
      bOffX = bOffStable
    else
      local ratio = (et - cxg.redStart)/(cxg.redFix - cxg.redStart)
      rOffX = -offSetOut + (offSetOut + rOffStable) * ratio
      bOffX = offSetOut - (offSetOut - bOffStable) * ratio
    end
    rVisible = true
    bVisible = true
  end


  if et >= cxg.mergeStart and et < cxg.mergeIn then
    local mergeOutOffX
    if et < cxg.mergeOut then
      mergeOutOffX = cxg.mergeOutSpan * (et - cxg.mergeStart) / (cxg.mergeOut - cxg.mergeStart)
    else
      mergeOutOffX = cxg.mergeOutSpan
    end

    rOffX = rOffX - mergeOutOffX
    bOffX = bOffX + mergeOutOffX

    if et >= cxg.mergeOut and et < cxg.mergeIn then
      local mergeInSpan = bOffX * (et - cxg.mergeOut) / (cxg.mergeIn - cxg.mergeOut)
      rOffX = rOffX + mergeInSpan
      bOffX = bOffX - mergeInSpan
    end
  end

  local flashRatio = 0
  if et >= cxg.flashIn and et < cxg.flashOut then
    if et >= cxg.mergeIn then
      flashRatio = (et - cxg.flashIn) / (cxg.mergeIn - cxg.flashIn)
    else
      flashRatio = (et - cxg.mergeIn) / (cxg.flashOut - cxg.mergeIn)
    end
  elseif et >= cxg.acqEnd and et < cxg.fxEnd then
    flashRatio = (et - cxg.acqEnd) / (cxg.fxEnd - cxg.acqEnd)
  end
  for i = 0, tic.colors*3 - 1 do
    local comp = fx.context.palette[1+i]
    poke(tic.ram.palette + i, comp + (255-comp)*flashRatio)
  end

  if et >= cxg.mergeIn then
    yVisible = true
    yOffX = 0
  end


  if rVisible then
    glenzDraw(et, fx.context.glenz, 0.15, -40, rOffX, 0, 8, 9, 5, -0.03)
  end
  if gVisible then
    glenzDraw(et, fx.context.glenz, 0.20, 20, gOffX, 0, 10, 11, 5, -0.05)
  end
  if bVisible then
    glenzDraw(et, fx.context.glenz, 0.15, -40, bOffX, 0, 12, 13, 5, -0.03)
  end
  if yVisible then
    glenzDraw(et, fx.context.glenz, 0.20, 20, yOffX, 0, 14, 15, 5, -0.05)
  end


  if et >= cxg.textIn then
    local t = math.min(1+(et - cxg.textIn)//800, #cxg.texts)
    local block = cxg.texts[t]
    local text = block[2]
    local tx = 10
    local ty = (tic.height - 12*block[1])/2
    print(text, tx, ty-1, 1, false, 2)
    print(text, tx, ty+1, 1, false, 2)
    print(text, tx-1, ty, 1, false, 2)
    print(text, tx+1, ty, 1, false, 2)
    print(text, tx, ty, 5, false, 2)
  end
end

