Drop.__index = Drop
function Drop:new()
  local inst = setmetatable({}, Drop)
  inst:reset()
  return inst
end

function Drop:reset(et)
  self.x = math.random(0, tic.width-1)  -- x coord
  self.p = math.random(2, 6) -- nb of pix
  self.s = math.random(700, 2000) -- time to cross the screen
  self.t = et or -math.random(0, self.s)
end

function Drop:y(et, height)
  height = height + self.p
  local y = height * (et - self.t) / self.s
  if y >= height then self:reset(et) end
  return math.floor(y)
end

