function endingInit(lt, fx)
  local cx = fx.context
  cx.text =  "ALL ENDINGS ARE ALSO BEGINNINGS. WE JUST DON'T KNOW IT AT THE TIME."
  cx.margin = 10
  cx.height = 116
  cx.dropCount = 120
  cx.dropTrans = { 1, 1, 2, 3, 4, 5, 6, 7, 8, 8, 11, 11, 12, 14, 14} -- dim to which color from which color with raindrops
  cx.dropTrans[0] = 9 -- damn 1-based arrays

  cx.background = endingImage
  tic.pokePalette(cx.background.palette)
  tic.pokeBorderColor(0)

  cx.scrollStart = 3300
  cx.flashInDuration = 1000
  cx.fadeInDuration = 3000
  cx.fadeOutDuration = 2000
  cx.scalePalette = function(scale)
    scale = math.max(0, scale)
    result = {}
    for _,comp in ipairs(cx.background.palette) do
      table.insert(result, comp * scale)
    end
    return result
  end

  -- build drops
  cx.drops = {}
  for _=1, cx.dropCount do
    table.insert(cx.drops, Drop:new())
  end

  -- decompress mask (0xFFFFFFFFFFFF was "compressed" to -1)
  for i, l in ipairs(endingMask) do
    if l == -1 then endingMask[i] = 0xFFFFFFFFFFFF end
  end

  cx.isMasked = function(x, y)
    y = y - cx.margin -- mask doesn't include the margins
    if y < 0 or y >= cx.height then return true end

    local val = endingMask[1+y*5+x//48] -- mask is laid out as 5 * 48 bits per line
    return (val >> (x%48)) &1 == 1
  end

  -- glenz props
  fx.context.glenz = glenzInit(lt, fx)

  cx.drawBackground = function()
    for y=0, endingImage.height-1 do
      for x=0, endingImage.width-1 do
        if cx.isMasked(x, y) or pix(x, y) == 0 then
          local col = endingImage:pix(x, y)
          pix(x, y, col)
        end
      end
    end
  end
end

function endingFunc(lt, et, fx)
  local cx = fx.context
  cls()

  -- fade in
  if et < cx.flashInDuration then
    local flashRatio = (cx.flashInDuration-et) / cx.flashInDuration
    local pal = {}
    for i = 0, tic.colors*3 - 1 do
      local comp = cx.background.palette[1+i]
      table.insert(pal, comp + (255-comp)*flashRatio)
    end
    tic.pokePalette(pal)
  end
  --if et < cx.fadeInDuration then
  --  local pal = cx.scalePalette(et/cx.fadeInDuration)
  --  tic.pokePalette(pal)
  --end

  -- fade out
  local remaining = fx.duration - et
  if remaining <= cx.fadeOutDuration then
    local pal = cx.scalePalette(remaining/cx.fadeOutDuration)
    tic.pokePalette(pal)
  end

  -- draw scroll
  if et > cx.scrollStart then
    local scrollY = 58 + 15 * math.sin(math.twopi * et/3000)
    local scrollX = tic.width - 130*(et-cx.scrollStart)/1000
    print(cx.text, scrollX, scrollY, 1, false, 5)
    -- so now we have the scroll on a black background
  end

  -- draw glenz
  local gOffX = math.max(30, 240 - et/30)
  --glenzDraw(et, fx.context.glenz, 0.06, 20, gOffX-30, -40, 4, 6, 1, zThresh)
  glenzDraw(et, fx.context.glenz, 0.06, 60, gOffX, -30, 4, 6, 1, 0)
  glenzDraw(et, fx.context.glenz, 0.06, 120, gOffX+30, -20, 4, 6, 1, 0)

  -- draw background
  cx.drawBackground(0, 0)

  -- draw drops
  for _, drop in ipairs(cx.drops) do
    local dropOffY = drop:y(et, cx.height)
    for p=1,drop.p do
      local dropPixY = dropOffY + p
      if dropPixY >= 0 and dropPixY < cx.height then
        dropPixY = dropPixY + cx.margin
        --local col = pix(drop.x, dropPixY)
        local col = cx.background:pix(drop.x, dropPixY) -- we wanna draw the background over the scrolltext
        local newCol = cx.dropTrans[col]
        pix(drop.x, dropPixY, newCol)
      end
    end
  end
end


