function creditsInit(lt, fx)
  local cx = {
    credits1 = creditsImage1,
    credits2 = creditsImage2,
    credits3 = creditsImage3,
    fadeInDuration = 2000, -- duration
    holdDuration = 2000, -- duration
    fadeOutDuration = 2000, -- duration
    backColor = 15,
    dropCount = 120,
  }

  fx.context = cx

  tic.pokeBorderColor(cx.backColor)
end

function creditsFunc(lt, et, fx)
  local cx = fx.context
  cls(cx.backColor)

  local palette = {}
  local nbColors = cx.credits1:nbColors()
  local fadeOutStart = cx.fadeInDuration + cx.holdDuration
  local fxEnd = fadeOutStart + cx.fadeOutDuration

  if et < cx.fadeInDuration then
    local ratio = et/2000
    for i=0,nbColors-1 do
      local r, g, b = cx.credits1:color(i)
      table.insert(palette, r*ratio)
      table.insert(palette, g*ratio)
      table.insert(palette, b*ratio)
    end
  elseif et >= fadeOutStart then
    local ratio = (fxEnd-et)/2000
    for i=0,nbColors-1 do
      local r, g, b = cx.credits1:color(i)
      table.insert(palette, r*ratio)
      table.insert(palette, g*ratio)
      table.insert(palette, b*ratio)
    end
  else
    palette = cx.credits1.palette
  end

  if et < fxEnd then
    tic.pokePalette(palette)
    cx.credits1:draw(10, 62, function(x, y, col) pix(x, y, col) end)
    cx.credits2:draw(109, 61, function(x, y, col) pix(x, y, col) end)
    cx.credits3:draw(207, 61, function(x, y, col) pix(x, y, col) end)
  end
end

