function winkInit(lt, fx)
  tic.syncMemCopy(0, 1, tic.ram.sfxState, tic.ram.persistentMem-1)
  local delays = {2000, 100, 200, 100, 2000}

  local off = 0
  local times = {}
  for i, d in ipairs(delays) do
    off = off + d
    table.insert(times, off)
  end

  local cx = {
    times = times,
    frames = {0, 2, 4, 6, 0},
    imageNum = 1,
    sprSlots = 2,
    sprScale = 2,
    backColor = 4
  }
  cx.sprSize = cx.sprSlots * 8 * cx.sprScale

  fx.context = cx
  tic.pokeBorderColor(cx.backColor)
end

function winkFunc(lt, et, fx)
  local cx = fx.context

  if et >= cx.times[1] and not cx.sfxStarted then
    music(1, 0, 0, false)
    cx.sfxStarted = true
  end

  cls(cx.backColor)

  if cx.imageNum <= #cx.times then
    local x, y = (tic.width - cx.sprSize)/2, (tic.height - cx.sprSize)/2
    spr(cx.frames[cx.imageNum], x, y, -1, cx.sprScale, 0, 0, cx.sprSlots, cx.sprSlots)

    if et >= cx.times[cx.imageNum] then
      cx.imageNum = cx.imageNum+1
    end
  end
end

